mapper.select:
select

from rule


    and rule_name like concat('%', #{ruleName}, '%')


    and app_id = #{appId}


    and rule_identification like concat('%', #{ruleIdentification}, '%')


    and job_identification like concat('%', #{jobIdentificatio}, '%')


    and status = #{status}


    and triggering = #{triggering}
